body {
    position: relative;
    min-height: 500px; /* Replace 100vh with fixed height */
    padding-bottom: 100px;
    direction: ltr;
}

.footer {
    position: absolute; /* WeasyPrint has limited support for fixed positioning */
    bottom: 0;
    left: 0;
    width: 100%;
    background-color: white;
}

.content-wrapper {
    margin-bottom: 120px;
    direction: ltr;
}

/* Remove transform properties - not supported */
* {
    direction: ltr;
}

/* Divider Line */
.divider {
    border: 1px solid #000;
    margin: 10px 0;
}

/* Replace grid with table layout */
.header-grid {
    width: 100%;
    font-size: 14px;
    margin-bottom: 20px;
    direction: ltr;
}

.header-grid table {
    width: 100%;
    border-collapse: collapse;
}

.header-grid td {
    width: 50%;
    vertical-align: top;
    padding-right: 20px;
}

.grid-item {
    direction: ltr;
}

/* Replace flex with block layout */
.field {
    margin-bottom: 5px;
    direction: ltr;
    overflow: hidden; /* For clearfix */
}

.label {
    font-weight: bold;
    width: 150px;
    text-align: left;
    margin-left: 0;
    padding-left: 0;
    font-size: 14px;
    border-style: none;
    direction: ltr;
    float: left; /* Replace flex with float */
}

.value {
    text-align: left;
    direction: ltr;
    margin-left: 140px; /* Account for label width + spacing */
}

/* Items Table */
.items-table {
    width: 100%;
    border-collapse: collapse;
    font-family: Arial, sans-serif;
    font-size: 12px;
    margin-bottom: 15px;
    direction: ltr;
}

.items-table th,
.items-table td {
    text-align: center;
    direction: ltr;
    padding: 5px; /* Add some padding for better appearance */
}

.items-table th {
    text-align: center;
    font-weight: bold;
    color: white;
    background-color: #F3D211 !important;
    direction: ltr;
}

.text-right {
    text-align: right;
    direction: ltr;
}

/* Replace grid with table layout for summary */
.summary-section {
    width: 100%;
    direction: ltr;
}

.summary-section table {
    width: 100%;
    border-collapse: collapse;
}

.summary-section td {
    vertical-align: top;
}

.summary-section .col-35 {
    width: 35%;
}

.summary-section .col-15 {
    width: 15%;
}

.summary-section .label {
    font-weight: bold;
    text-align: left;
    font-size: 12px;
    direction: ltr;
}

.summary-section .value {
    text-align: right;
    font-size: 12px;
    direction: ltr;
}

.summary-section .empty {
    visibility: hidden;
}

.print-format.print-format-preview {
    font-size: 14px;
    padding: 25px; /* Convert 0.35in to approximate pixels */
    direction: ltr;
}

