css:
@page {
    size: A4;
    margin: 20mm;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

.quotation-print {
    font-family: 'Arial', 'Helvetica', sans-serif;
    color: #212529;
    line-height: 1.4;
    max-width: 100%;
    background: #fff;
}

/* Header Styling */
.header {
    text-align: center;
    padding: 20px 0 25px;
    border-bottom: 1px solid #212529;
    margin-bottom: 25px;
}

.header h1 {
    font-size: 24px;
    font-weight: 700;
    margin-bottom: 15px;
    color: #212529;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.header .doc-info {
    display: flex;
    justify-content: flex-end;
    margin-top: 15px;
    font-size: 12px;
    color: #495057;
    gap: 30px;
}

.header .doc-info > div {
    text-align: right;
}

.header .doc-info strong {
    font-weight: 600;
    color: #212529;
}

/* Section Styling */
.section {
    margin-bottom: 25px;
    page-break-inside: avoid;
}

.section-title {
    font-size: 14px;
    font-weight: 700;
    padding: 8px 0;
    margin-bottom: 15px;
    color: #212529;
    border-bottom: 1px solid #212529;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Info Grid Layout - 2 Column Layout */
.info-grid {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.info-row {
    display: flex;
    justify-content: space-between;
    gap: 30px;
    align-items: flex-start;
    padding: 4px 0;
}

.info-field {
    flex: 1;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.info-label {
    font-weight: 600;
    color: #212529;
    font-size: 12px;
    min-width: 140px;
}

.info-value {
    color: #495057;
    font-size: 12px;
    text-align: right;
    flex: 1;
    margin-left: 10px;
}

/* Company Info Layout */
.company-info {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 15px;
}

.company-details {
    text-align: center;
    flex: 1;
}

.company-name {
    font-size: 14px;
    font-weight: 600;
    color: #212529;
    margin-bottom: 5px;
}

.company-extra {
    font-size: 11px;
    color: #495057;
}

/* Items Table */
.items-table {
    width: 100%;
    border-collapse: collapse;
    margin: 15px 0;
    border: 1px solid #dee2e6;
}

.items-table th {
    padding: 10px 8px;
    text-align: center;
    border: 1px solid #dee2e6;
    background: #f8f9fa;
    color: #212529;
    font-weight: 700;
    font-size: 12px;
}

.items-table td {
    padding: 8px;
    text-align: center;
    border: 1px solid #dee2e6;
    font-size: 12px;
    background: #fff;
    color: #495057;
}

.items-table tbody tr:nth-child(even) {
    background: #fafafa;
}

.items-table .item-name,
.items-table .item-description {
    text-align: left;
    padding-left: 10px;
}

.items-table .number {
    text-align: right;
    padding-right: 10px;
    font-variant-numeric: tabular-nums;
}

/* Totals Container */
.totals-container {
    display: flex;
    justify-content: flex-end;
    margin: 20px 0;
}

.totals-table {
    width: 300px;
    border-collapse: collapse;
}

.totals-table tr {
    border: none;
}

.totals-table td {
    padding: 6px 12px;
    border: none;
    font-size: 12px;
}

.totals-label {
    text-align: right;
    font-weight: 600;
    color: #212529;
    padding-right: 15px;
}

.totals-value {
    text-align: right;
    color: #495057;
    font-weight: 500;
    font-variant-numeric: tabular-nums;
}

.grand-total-row td {
    border-top: 1px solid #212529;
    border-bottom: 1px solid #212529;
    padding: 8px 12px;
    font-weight: 700;
    font-size: 14px;
    color: #212529;
}

/* Payment Mode Section */
.payment-pos {
    margin: 15px 0;
}

.payment-info {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    padding: 10px;
    background: #f8f9fa;
    border: 1px solid #dee2e6;
}

.payment-label {
    font-weight: 600;
    color: #212529;
    font-size: 12px;
}

.payment-value {
    color: #495057;
    font-size: 12px;
    font-weight: 500;
}

.payment-logos {
    display: flex;
    justify-content: center;
    gap: 30px;
    align-items: center;
    padding: 15px;
    border: 1px solid #dee2e6;
    background: #fff;
}

.logo-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 5px;
}

.payment-logo {
    width: 40px;
    height: 25px;
    object-fit: contain;
}

.logo-label {
    font-size: 10px;
    color: #495057;
    font-weight: 500;
}

/* Bank Details */
.payment-bank {
    margin: 15px 0;
}

.bank-details {
    border: 1px solid #dee2e6;
    background: #f8f9fa;
    padding: 15px;
}

.bank-title {
    font-size: 14px;
    font-weight: 700;
    color: #212529;
    margin-bottom: 15px;
    text-align: center;
    text-transform: uppercase;
}

.bank-info-grid {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.bank-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 5px 0;
}

.bank-label {
    font-weight: 600;
    color: #212529;
    font-size: 12px;
    min-width: 180px;
}

.bank-value {
    color: #495057;
    font-size: 12px;
    font-weight: 500;
    text-align: right;
    flex: 1;
}

/* Terms and Conditions - Clean Style */
.terms-section {
    margin-top: 30px;
    margin-bottom: 20px;
}

.terms-content {
    margin-top: 10px;
    padding: 0;
    background: transparent;
    border: none;
    font-size: 11px;
    line-height: 1.6;
}

.terms-list {
    margin: 0;
    padding-left: 20px;
}

.terms-list li {
    margin-bottom: 10px;
    color: #212529;
    text-align: justify;
    padding-right: 10px;
}

.terms-list li:last-child {
    margin-bottom: 0;
}

/* Footer */
.footer {
    margin-top: 30px;
    padding: 10px 0;
    border-top: 1px solid #212529;
    text-align: center;
    font-size: 9px;
    color: #495057;
}

.footer p {
    margin: 2px 0;
    line-height: 1.4;
}

